[15]={link_id=15,msg="巅峰对决前20分钟是{string_2|219|单人PK}，后25分钟是{string_2|219|团队战斗}，可激烈咧。达到一定段位，就有{string_2|219|段位奖励}，赛季结算又有{string_2|219|结算奖励}，前50名玩家还有额外的{string_2|219|排名奖励}，保管让你拿不停！",voice=""},
[16]={link_id=16,msg="剑界秘境里，我们可以{string_2|219|上交名剑}攻击其它阵营，也可以{string_2|219|上交仙桃}恢复自己阵营的生命。{string_2|219|击杀其它阵营的怪物}或者{string_2|219|抢夺玩家}就能获得名剑，清楚了吗？",voice=""},
