[635]={link_id=635,msg="乘风:\n\n提高躲闪。",lev=999},
[636]={link_id=636,msg="惊雷:\n\n提高暴击。",lev=999},
[637]={link_id=637,msg="归元:\n\n提高抗暴。",lev=999},
[638]={link_id=638,msg="天照:\n\n提高伤害。",lev=999},
[639]={link_id=639,msg="龟麟:\n\n提高免伤。",lev=999},
[640]={link_id=640,msg="瑶光:\n\n提高控制。",lev=999},
[641]={link_id=641,msg="磐石:\n\n提高抗控。",lev=999},
[642]={link_id=642,msg="追风:\n\n提高速度。",lev=999},
[643]={link_id=643,msg="诛仙:\n\n提高攻击。",lev=999},
[644]={link_id=644,msg="金圣:\n\n提高气血。",lev=999},
[645]={link_id=645,msg="天命包括：\n1、{diclink_1|219|【两仪】|630}   {diclink_1|219|【罡气】|631}  {diclink_1|219|【猬甲】|632}\n\n2、{diclink_1|219|【碎骨】|633}    {diclink_1|219|【陨日】|634}    {diclink_1|219|【乘风】|635}\n\n3、{diclink_1|219|【惊雷】|636}    {diclink_1|219|【归元】|637}    {diclink_1|219|【天照】|638}\n\n4、{diclink_1|219|【龟麟】|639}    {diclink_1|219|【瑶光】|640}    {diclink_1|219|【磐石】|641}\n\n5、{diclink_1|219|【追风】|642}    {diclink_1|219|【诛仙】|643}    {diclink_1|219|【金圣】|644}\n可用于装备玩家和伙伴。",lev=999},
[646]={link_id=646,msg="天命之塔：\n\n{string_2|219|34}级后开启的爬塔类玩法，挑战天命塔可解锁天命类型，并获得{string_2|219|天命}、{string_2|219|天命精华}、{string_2|219|伙伴护符}等道具。",lev=999},
[647]={link_id=647,msg="天命精华：\n\n可用于升级{diclink_1|219|【天命】|645}，挑战/扫荡{string_2|219|天命之塔}获得。炼化天命也可获得。",lev=999},
[648]={link_id=648,msg="天命商城：\n\n消耗{diclink_1|219|【天命碎片】|650}购买天命的商城，可通过{string_2|219|道具-天命-天命商城}打开。",lev=999},
[649]={link_id=649,msg="",lev=999},
[650]={link_id=650,msg="天命碎片：\n\n可在{diclink_1|219|【天命商城】|648}购买指定天命，在{diclink_1|219|【寻仙阁】|653}寻觅天命时获得。",lev=999},
[651]={link_id=651,msg="寻仙符：\n\n可在{diclink_1|219|【寻仙阁】|653}寻觅1次天命。",lev=999},
[652]={link_id=652,msg="寻仙令：\n\n可在{diclink_1|219|【寻仙阁】|653}寻觅10次天命。",lev=999},
[653]={link_id=653,msg="寻仙阁：\n\n消耗{diclink_1|219|【寻仙符】|651}或者{diclink_1|219|【寻仙令】|652}来寻觅天命，可通过{string_2|219|道具-天命-寻仙阁}打开。",lev=999},
[680]={link_id=680,msg="无损继承的成长系统包括：\n\n1、{diclink_1|219|【装备强化】|681}  {diclink_1|219|【装备镶嵌】|683}\n\n2、{diclink_1|219|【道行-完美传承】|689}",lev=999},
[681]={link_id=681,msg="装备强化：\n\n消耗{string_2|219|强化石}来加强装备属性，每次完美激活{string_2|219|+3}、{string_2|219|+6}、{string_2|219|+9}、{string_2|219|+12}时，都会获得额外属性加成。\n\n切换装备时，强化等级可{string_2|219|完美继承}。",lev=999},
[682]={link_id=682,msg="装备洗炼：\n\n消耗{string_2|219|洗练石}来刷新装备固有属性，较低概率洗炼属性会比原来属性低，{string_2|219|40级}以上紫色装备可进行洗炼。\n\n分解装备时，洗炼石会{string_2|219|有损返还}。",lev=999},
[683]={link_id=683,msg="装备镶嵌：\n\n结丹境界开启，使用{diclink_1|219|【金刚钻】|688}对装备打孔后，可镶嵌对应宝石。可镶嵌宝石和打孔个数，与装备强化等级有关。\n\n切换装备时，镶嵌宝石可{string_2|219|完美继承}。",lev=999},
[684]={link_id=684,msg="道行：\n\n炼虚境界开启，开启后可通过消耗{string_2|219|真气}和{string_2|219|九转仙丹}提升道行。道行越高，与玩家对战时控制概率和伤害就越高。\n\n道行可进行{diclink_1|219|【普通传承】|690}或{diclink_1|219|【完美传承】|689}。",lev=999},
[685]={link_id=685,msg="强化石:\n\n装备强化{219|+1}至{219|+8}的必备之物，可合成为高级强化石。",lev=1},
[686]={link_id=686,msg="高级强化石：\n\n装备强化{219|+9}至{219|+14}的必备之物，可合成为极品强化石。",lev=1},
[687]={link_id=687,msg="极品强化石：\n\n装备强化{219|+15}至{219|+20}的必备之物。",lev=1},
[688]={link_id=688,msg="金刚钻：\n\n使用后可开启装备部位的镶嵌孔位。",lev=999},
[689]={link_id=689,msg="道行-完美传承：\n\n消耗仙玉，将一个伙伴的道行{string_2|219|100%}的传承到另一个伙伴上。原伙伴如果已有道行，会先清除已有的再继承传承的道行。",lev=999},
[690]={link_id=690,msg="道行-普通传承：\n\n消耗金贝，将一个伙伴的道行{string_2|219|90%}的传承到另一个伙伴上。原伙伴如果已有道行，会先清除已有的再继承传承的道行。",lev=999},
[691]={link_id=691,msg="有损继承的成长系统包括：\n\n1、{diclink_1|219|【装备洗炼】|682}  {diclink_1|219|【道行-普通既传承】|690}",lev=999},
[692]={link_id=692,msg="真气：\n\n提升道行和修为境界的道具，可通过{string_2|219|主线任务}、{string_2|219|门派比武}、{string_2|219|三界妖王}、{string_2|219|天命之塔}获得。",lev=999},
[693]={link_id=693,msg="九转仙丹：\n\n用于{string_2|219|道行突破}必备的仙丹。",lev=999},
[730]={link_id=730,msg="游戏中的阵法有：\n1、{diclink_1|219|【七星阵】|731}  {diclink_1|219|【密林阵】|732}  {diclink_1|219|【急流阵】|733}\n\n2、{diclink_1|219|【玄火阵】|734}  {diclink_1|219|【山岳阵】|735}  {diclink_1|219|【金锁阵】|736}\n\n3、{diclink_1|219|【地绝阵】|737}\n\n提升境界可依次解锁阵法，收集阵法书可以提升对应阵法等级。",lev=999},
[731]={link_id=731,msg="七星阵，均衡型阵法。\n2号位{string_2|219|免伤率+3%（满级+12%）}\n\n4、6号位{string_2|219|速度+18（满级+54）}\n\n7、9号位伤害率{string_2|219|+3%（满级+12%）}\n\n克制{diclink_1|219|【密林阵】|732}。",lev=999},
[732]={link_id=732,msg="密林阵，治疗型阵法。\n1、2、3号位{string_2|219|被治疗+3%（满级+12%）}，{string_2|219|免伤率+6%（满级+12%）}\n\n5号位{string_2|219|治疗+5%（满级+18.5%）}|\n\n8号位{string_2|219|加被治疗+3%（满级+12%）}，{string_2|219|速度+12（满级+39）}\n\n克制{diclink_1|219|【山岳阵】|735}和{diclink_1|219|【地绝阵】|737}。",lev=999},
[733]={link_id=733,msg="急流阵，速度型阵法。\n1、4、8号位{string_2|219|速度+18（满级+54）}\n\n3、6号位{string_2|219|速度+24（满级+57）}，{string_2|219|免伤率-3%（满级-5.7%）}\n\n克制{diclink_1|219|【玄火阵】|734}。",lev=999},
[734]={link_id=734,msg="玄火阵，攻击型阵法。\n2号位{string_2|219|伤害率+3%（满级+12%）}\n\n4、5、6、8号位{string_2|219|伤害率+5%（满级+18.5%）}，{string_2|219|速度-12（满级-30）}\n\n克制{diclink_1|219|【七星阵】|731}和{diclink_1|219|【金锁阵】|736}。",lev=999},
[735]={link_id=735,msg="山岳阵，免伤型阵法。\n1、3、5号位{string_2|219|免伤率+5%（满级+18.5%）}，{string_2|219|速度-12（满级-30）}\n\n7、9号位{string_2|219|免伤率-2%（满级-6.5%）}，{string_2|219|伤害率+2%（满级+6.5%）}\n\n克制{diclink_1|219|【急流阵】|733}。",lev=999},
[736]={link_id=736,msg="金锁阵，控制型阵法。\n1、8号位{string_2|219|控制+2%（满级+6.5%）}，{string_2|219|速度+12（满级+39）}\n\n3号位{string_2|219|伤害率+3%（满级+12%）}\n\n7、9号位{string_2|219|伤害率+3%（满级+18.5%）}、{string_2|219|免伤率-3%（满级-5.7%）}\n\n克制{diclink_1|219|【密林阵】|732}。",lev=999},
[737]={link_id=737,msg="地绝阵，抗控型阵法。\n2、5号位{string_2|219|抗控率+3%（满级+12%）}\n\n7、8、9号位{string_2|219|抗控率+3%（满级+6.5%）}，{string_2|219|伤害率+2%（满级+6.5%）}\n\n克制{diclink_1|219|【急流阵】|733}。",lev=999},
[739]={link_id=739,msg="十二星君：\n\n每天{string_2|219|九点到十二点}，每逢半点将在两个场景刷新星君。通过主界面左上角的{string_2|219|【野外】}按钮，可以快速找到星君所在地图。\n\n46级后组成{string_2|219|3人以上}的队伍即可挑战星君，{string_2|219|1-3星}的星君组成队伍后可直接挑战，{string_2|219|4星以上}的星君需要队长有挑战资格。",lev=999},
[740]={link_id=740,msg="",lev=999},
[741]={link_id=741,msg="",lev=999},
[750]={link_id=750,msg="家园：\n\n40级以后，可以通过长安【NPC豪绅】或主界面【家园】按钮，购买家园，开启家园玩法。开启家园后，可进行{diclink_1|219|【烹饪】|751}、{diclink_1|219|【采矿】|752}、{diclink_1|219|【炼丹】|753}等玩法。",lev=999},
[751]={link_id=751,msg="烹饪：\n\n消耗活力生产{diclink_1|219|【主食】|754}、{diclink_1|219|【点心】|755}。通过消耗部落贡献可升级建筑，解锁更高级的烹饪产物。",lev=999},
[752]={link_id=752,msg="采矿：\n\n消耗活力进行采集{diclink_1|219|【秘银矿】|778}、{diclink_1|219|【紫晶矿】|779}。通过消耗部落贡献可升级建筑，解锁更高级的{diclink_1|219|【矿产】|756}。",lev=999},
[753]={link_id=753,msg="炼丹：\n\n消耗活力可生产对应门派的专精丹药，丹药分别有{diclink_1|219|【大力丸】|790}、{diclink_1|219|【天元丹】|781}、{diclink_1|219|【活血膏】|782}、{diclink_1|219|【神行散】|783}、{diclink_1|219|【聚气散】|784}。通过消耗部落贡献可升级建筑，提升丹药的等级。",lev=999},
[754]={link_id=754,msg="主食包括：\n1、{diclink_1|219|【桂花糕】|758}  {diclink_1|219|【冰镇秋葵】|768}  {diclink_1|219|【臭豆腐】|760}\n\n2、{diclink_1|219|【酒酿丸子】|762}  {diclink_1|219|【八宝粥】|764}\n\n3、{diclink_1|219|【阳澄湖大闸蟹】|777}  {diclink_1|219|【金牌虾饺】|766}",lev=999},
