[30010]={link_id=30010,msg="红装：\n\n红色装备是各种颜色装备当中最好的一类装备。",lev=1},
[30020]={link_id=30020,msg="装备获得：\n\n装备可以通过打造或装备副本获得。",lev=1},
[30030]={link_id=30030,msg="属性：\n\n包括{diclink_1|219|【生命】|30040}、{diclink_1|219|【物攻】|30041}、{diclink_1|219|【物防】|30042}、{diclink_1|219|【控制】|30043}、{diclink_1|219|【破甲】|30044}、{diclink_1|219|【速度】|30045}、{diclink_1|219|【暴击】|30046}、{diclink_1|219|【法防】|30047}、{diclink_1|219|【抗控】|30048}、{diclink_1|219|【抗暴】|30049}等项，各项属性决定了战斗时的表现，是战场决胜至关重要的因素。",lev=1},
[30040]={link_id=30040,msg="生命：\n\n决定了您在战场上的生存能力，生命值为零时，您的角色或宠物将会在战场上失去战斗能力。",lev=1},
[30041]={link_id=30041,msg="物攻：\n\n即物理攻击，此项数值越高，物理攻击效果越好。",lev=1},
[30042]={link_id=30042,msg="物防：\n\n此项数值决定了您对于物理攻击的防御能力。",lev=1},
[30043]={link_id=30043,msg="控制：\n\n此项数值决定了您对敌方的控制能力，数值越高，控制敌方的概率越高。",lev=1},
[30044]={link_id=30044,msg="破甲：\n\n此项数值可以让您一定程度忽略对方的防御能力，输出伤害。",lev=1},
[30045]={link_id=30045,msg="速度：\n\n此项数值决定了您在战场中的出手速度。",lev=1},
[30046]={link_id=30046,msg="暴击：\n\n战场中若是出现暴击，则能够为您的攻击带来极强的增幅效果，此项数值越高，暴击出现的概率越高。",lev=1},
[30047]={link_id=30047,msg="法防：\n\n此项数值决定了您在战场中抵抗法术伤害的能力。",lev=1},
[30048]={link_id=30048,msg="抗控：\n\n此项数值决定了您在战场中应对敌方的控制的免疫概率。",lev=1},
[30049]={link_id=30049,msg="抗暴：\n\n此项数值可以提高您在战场中消除敌方暴击的概率。",lev=1},
[30055]={link_id=30055,msg="装备鉴定：\n\n未鉴定的装备需要鉴定以后才能确定它的具体属性值，装备鉴定以后无法再上架售卖。",lev=1},
[30060]={link_id=30060,msg="商城：\n\n商城分为神秘商店、每日优惠等模块，可以在这里通过石币或钻石，购买到您所需要的物品。",lev=1},
[30061]={link_id=30061,msg="积分商城：\n\n分荣誉、战功、助战、宠物、幸运等多个模块，可用各种积分兑换指定的物品。",lev=1},
[30062]={link_id=30062,msg="庄园商城：\n\n对应土、森、火、龙四大庄园的商城，可通过钻石购买对应物品。",lev=1},
[30070]={link_id=30070,msg="排行榜：\n\n里面记录了各种实力榜单，是了解强者信息最有效的窗口。",lev=1},
[30080]={link_id=30080,msg="部落：\n\n加入部落等同于让您寻找到了一个友爱的组织，结交部落的小伙伴们，能够极大地增加您在游戏中与他人的交互哟，同时也更能够帮助您快速融入游戏和了解游戏的更多内容。",lev=1},
[30090]={link_id=30090,msg="储备气血：\n\n战斗会导致气血减损，若有储备气血，在战斗之后则会自动恢复您的气血值，直到补充完满。",lev=1},
[30095]={link_id=30095,msg="宠物：\n\n是十分重要的一种战斗资源，不同的宠物，有着不同的能力特性，也可以根据喜好进行指定培养，合理的宠物搭配将会起到意想不到的加成效果哦。",lev=1},
[30100]={link_id=30100,msg="人物等级、宠物等级与世界等级：\n\n宠物等级不能超过人物等级，人物等级不能超过世界等级，世界等级随着现实时间的推移，将逐步开放更高等级。",lev=1},
[30110]={link_id=30110,msg="宠物技能书：\n\n可用于宠物学习和升级后天技能。",lev=1},
[30120]={link_id=30120,msg="洗练石：\n\n洗练紫色和橙色品质装备的附加属性的必备材料。",lev=1},
[30140]={link_id=30140,msg="宠物经验：\n\n可用于指定宠物的升级操作，是宠物变强极为重要的一种资源。",lev=1},
[30150]={link_id=30150,msg="经验加成：\n\n人物低于世界等级一定额度时，在获取经验的时候将会有一定程度的经验获取加成。",lev=1},
[30160]={link_id=30160,msg="经验：\n\n可通过各类任务获得，主要作用于对应目标的等级提升。",lev=1},
[30170]={link_id=30170,msg="关于升级：\n\n人物或宠物升级后，整体属性都会得到加强哦，是变强最重要和最直接的一种方式。",lev=1},
[30180]={link_id=30180,msg="人物职业：\n\n分为多种，50级转职以后，可以选择两种不同的职业方向哦，不同的职业有不同的技能，您想了解哪个职业的特点呢？{diclink_1|219|【战士】|30181}、{diclink_1|219|【守卫】|30182}、{diclink_1|219|【勇士】|30183}、{diclink_1|219|【术士】|30184}、{diclink_1|219|【法师】|30185}、{diclink_1|219|【祭司】|30186}、{diclink_1|219|【猎人】|30187}、{diclink_1|219|【神射】|30188}、{diclink_1|219|【兽王】|30189}。",lev=1},
[30181]={link_id=30181,msg="战士职业特点：防御反击，挑衅控制。",lev=0},
[30182]={link_id=30182,msg="守卫职业特点：防守援护，愈战愈勇。",lev=0},
[30183]={link_id=30183,msg="勇士职业特点：不屈狂战，血战不止。",lev=0},
[30184]={link_id=30184,msg="术士职业特点：法术群攻，治疗复活。",lev=0},
[30185]={link_id=30185,msg="法师职业特点：法术群攻，灼烧爆发。",lev=0},
[30186]={link_id=30186,msg="祭司职业特点：诅咒伤害，治疗复活。",lev=0},
[30187]={link_id=30187,msg="猎人职业特点：物理输出，单体爆发。",lev=0},
[30188]={link_id=30188,msg="神射职业特点：单体爆发，净化标记。",lev=0},
[30189]={link_id=30189,msg="兽王职业特点：号令群兽，驱散沉默。",lev=0},
[30190]={link_id=30190,msg="战力：\n\n是最为直接体现强弱的一项参考数值，提升战力将会使您变得更加强大。",lev=1},
[30200]={link_id=30200,msg="聊天框：\n\n游戏界面左下角的聊天框，是游戏信息集散的一个窗口，里面能够找到各个频道的相关信息，是能与整个游戏世界交互必不可少的一个信息窗口。",lev=1},
[30210]={link_id=30210,msg="每日任务：\n\n",lev=1},
[30220]={link_id=30220,msg="竞技场：\n\n是玩家对模拟玩家的一种竞技模式，玩家查看对手的综合战力，并可以通过刷新来选择对手，通过战斗获取功勋。",lev=1},
[30230]={link_id=30230,msg="图鉴：\n\n分为【图鉴总览】【宠物图鉴】【景点图鉴】【龙兽图鉴】和【成就图鉴】。",lev=1},
[30240]={link_id=30240,msg="功勋：\n\n游戏积分的一种，可用于换取功勋宝箱等奖励。",lev=1},
[30250]={link_id=30250,msg="狩猎龙兽：\n\n",lev=1},
[30260]={link_id=30260,msg="疯狂派对：\n\n将在每天中午12：10进行哦，更多休闲玩法尽在其中，精彩不容错过。",lev=1},
[30270]={link_id=30270,msg="海岛战场：\n\n将在每天晚上19：00进行哦，奖励多多，让我们一起为阵营而战吧！",lev=1},
[30280]={link_id=30280,msg="庄园争夺：\n\n每周二、四晚上20：00，部落之间将进行庄园争夺活动，夺得后庄园后，每天将会给部落的每位成员带来一定程度的钻石收益哦。",lev=1},
[30300]={link_id=30300,msg="集结使命：\n\n此活动将在每周一、三、五晚上21：00进行，与各地勇士一起前往指定地点，解救当地的危机吧！",lev=1},
[30310]={link_id=30310,msg="元素圣殿：\n\n分有火、土、水、暗等不同属性的元素圣殿，每个元素圣殿内有许多关卡，通关后将获得丰厚奖励。",lev=1},
[30320]={link_id=30320,msg="极速游猎：\n\n加速闯荡勇士之路，能给您带来大量收益。",lev=1},
[30330]={link_id=30330,msg="奇遇：\n\n分为{link_1|219|【宠物传记】|1030004}、{diclink_1|219|【区域事件】|30441}和{diclink_1|219|【集结使命】|30441}三种类型，您想了解哪种？",lev=1},
[30340]={link_id=30340,msg="勇士之路：\n\n里面有许多的章节和关卡，通关后可获得相应的奖励，是体现战斗实力的途径之一。",lev=1},
[30350]={link_id=30350,msg="挂机：\n\n可以在指定的地图区域进行挂机巡逻，打怪获取经验等奖励。",lev=1},
[30360]={link_id=30360,msg="委托任务：\n\n",lev=1},
[30370]={link_id=30370,msg="红装副本：\n\n组队挑战此副本，有几率获得大量高品质角色装备和珍贵的红装精华。",lev=1},
[30390]={link_id=30390,msg="跳舞：\n\n跟随草裙海獭或草裙海獭形象的目标，进行跳舞操作，从而获取奖励。",lev=1},
[30400]={link_id=30400,msg="魔化龙兽：\n\n",lev=1},
[30410]={link_id=30410,msg="天际探宝：\n\n从伊甸城莱拉处进入天际探宝活动，可以通过摇色子决定行进步数，从而探索路径获取对应的奖励哦。",lev=1},
[30420]={link_id=30420,msg="宠物星数：\n\n\n\n宠物星级越高，对应的宠物一般可以认为越强力哦，因此培养宠物应该优先选择星级高的，另外，宠物也是可以提升星数的，这种方式也可以使得您的爱宠变得更加强大哟。",lev=1},
[30440]={link_id=30440,msg="超值福利：\n\n分为{diclink_1|219|【每日签到】|30441}、{diclink_1|219|【投资计划】|30442}、{diclink_1|219|【月卡福利】|30443}、{diclink_1|219|【等级礼包】|30444}、{diclink_1|219|【幸运转盘】|30445}、{diclink_1|219|【特权礼包】|30446}等，您想了解哪种？",lev=1},
[30441]={link_id=30441,msg="每日签到：\n\n每日登陆可以进行的签到活动，获取指定奖励。",lev=1},
[30442]={link_id=30442,msg="投资计划：\n\n投资一定金额后，通过勇士之路的指定关卡，将会获得大量钻石奖励。",lev=1},
[30443]={link_id=30443,msg="月卡福利：\n\n累计充值达到指定额度即可激活，每日可领相应奖励，有效期30天。",lev=1},
[30444]={link_id=30444,msg="等级礼包：\n\n达到一定等级后，可以使用钻石开启等级礼包，获取对应的丰厚奖励。",lev=1},
[30445]={link_id=30445,msg="幸运转盘：\n\n分为幸运转盘和惊喜转盘两种，可以抽取幸运奖励。",lev=1},
[30446]={link_id=30446,msg="特权礼包：\n\n通过钻石进行激活，包含极速游猎特权、疾风快递特权、闪电快递特权，其中两项快递特权激活后永久生效，游猎特权有效期为30天。",lev=1},
